import {Card, Button, Form} from 'react-bootstrap';
import { useState } from 'react';
import { supabase } from './supabaseClient';

function FlightCard(props) {
    const flight = props.flight;

    const[ editing, setEditing ] = useState(false);
    const [ startloc, setStartloc ] = useState("");
    const [ endloc, setEndloc] = useState("");
    const [ passengers, setPassengers ] = useState("");

    async function updateFlight(){
        try{
            const {data, error } = await supabase
              .from("flights")
              .insert({
                // Content to the left of the : is the name of colum in DB
                // Content to the right of the : is the name of the const variable above
                startloc: startloc,
                endloc: endloc,
                passengers: passengers
              })
              .single()
      
            if (error) throw error;
            window.location.reload();
          }catch (error){
            alert(error.message);
          }
    }

    async function deleteFlight(){

    }

    return (
        <Card style={{width:"18rem"}}>
            <Card.Body>
                { editing == false ?
                    <>
                    <Card.Title>{flight.starloc}</Card.Title>
                    <Card.Text>{flight.endloc}</Card.Text>
                    <Card.Text>{flight.passengers}</Card.Text>
                    <Button variant="danger" onClick={() => deleteFlight()}> Delete Flight</Button>
                    <Button onClick={() => setEditing(true)}>Edit Flight</Button>
                    </>
                :
                    <>
                        <h4>Editing Flight</h4>
                        <Button size="sm" onClick={() => setEditing(false)}> Done Edting</Button>
                        <br></br>
                        <Form.Label></Form.Label>
                        <Form.Label>Start Location</Form.Label>
                        <Form.Control
                            type="text"
                            id="startloc"
                            defaultValue={flight.starloc}
                            onChange={(e)=> setStartloc(e.target.value)}
                        />
                        <Form.Label>End Location</Form.Label>
                        <Form.Control
                            type="text"
                            id="endloc"
                            defaultValue={flight.endloc}
                            onChange={(e)=> setEndloc(e.target.value)}
                        />
                        <Form.Label># of Passengers</Form.Label>
                        <Form.Control
                            type="int"
                            id="passengers"
                            defaultValue={flight.passengers}
                            onChange={(e) => setPassengers(e.target.value)}
                        />
                        <br></br>
                        <Button onClick={() => updateFlight()}>Update Flight Information</Button>
                    </>
                }
            </Card.Body>
        </Card>
    )
}

export default FlightCard;
