import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Container, Nav, Form, Row, Col, Button, Dropdown, DropdownButton } from 'react-bootstrap';
import { useState, useEffect } from 'react';
import FlightCard from './flightCard';
import { supabase } from './supabaseClient';
import InputGroup from 'react-bootstrap/InputGroup';

//YbuNRL9dMSQzMed1

// Create the user interface (Navbar, Form to create products, product card)
// Setup, create table for our products
// Implement the CRUD logic for products

// Resume video https://www.youtube.com/watch?v=4yVSwHO5QHU @ 14:22

function App() {
  const [ startloc, setStartloc ] = useState("");
  const [ endloc, setEndloc] = useState("");
  const [ passengers, setPassengers] = useState("");
  const [ flights, setFlights ] = useState([]);
  const [ value, setValue ] = useState("");
  const [ startdate, setStartDate ] = useState();
  const [ enddate, setEndDate ] = useState();
  const options = [
    {label: "One Way", value: "One Way"},
    {label: "Round Trip", value: "Round Trip"}
  ]

  console.log(startloc);
  console.log(endloc);
  console.log(passengers);

  useEffect(() => {
    getFlights();
  }, [])

  async function getFlights(){
    try{
      const {data, error } = await supabase
        .from("flights")
        .select("*")
        .limit(10)
      if (error) throw error;
      if (data != null){
        setFlights(data);
      }
    }catch (error){
      alert(error.message);
    }
  }

  async function createFlight() {
    try{
      const { data, error } = await supabase
        .from("flights")
        .insert({
          // Content to the left of the : is the name of colum in DB
          // Content to the right of the : is the name of the const variable above
          startloc: startloc,
          endloc: endloc,
          passengers: passengers,
          type: value,
          startday: startdate,
          endday: enddate
        })
        .single()

      if (error) throw error;
      window.location.reload();
    }catch (error){
      alert(error.message);
    }
  }

  console.log(flights);

  return (
    <>
      <Container>
        <hr></hr>
        <h3>Current Flights in Database</h3>
        <Row xs={1} lg={3} className="g-4">
          {flights.map((flight) => (
            <Col>
              <FlightCard flight={flight} />
            </Col>
          ))}
        </Row>
      </Container>    
    </>
  );
}

export default App;
